declare -r curr_path=$(dirname $(readlink -f "$0"))
source ../helpers.sh

function test_label()
{
  local ret=0

  isula run -tid --name annotation  --runtime $1 --label "test_long_" busybox sh
  [[ $? -ne 0 ]] && msg_err "${FUNCNAME[0]}:${LINENO} - failed to run container for long label" && ((ret++))

  isula inspect annotation
  [[ $? -ne 0 ]] && msg_err "${FUNCNAME[0]}:${LINENO} - failed to inspect container for long label" && ((ret++))

  isula exec -ti annotation echo 1
  [[ $? -ne 0 ]] && msg_err "${FUNCNAME[0]}:${LINENO} - failed to exec in container" && ((ret++))

  check_valgrind_log
  [[ $? -ne 0 ]] && msg_err "${FUNCNAME[0]}:${LINENO} - stop isulad failed" && ((ret++))

  start_isulad_with_valgrind
  [[ $? -ne 0 ]] && msg_err "${FUNCNAME[0]}:${LINENO} - start isulad failed" && ((ret++))

  isula rm -f annotation
  [[ $? -ne 0 ]] && msg_err "${FUNCNAME[0]}:${LINENO} - failed to rm -f container" && ((ret++))

  return ${ret}
}

function test_annotation()
{
  local ret=0

  isula run -tid --name annotation --runtime $1  --annotation "test_long_" busybox sh
  [[ $? -ne 0 ]] && msg_err "${FUNCNAME[0]}:${LINENO} - failed to run container for long label" && ((ret++))

  isula inspect annotation
  [[ $? -ne 0 ]] && msg_err "${FUNCNAME[0]}:${LINENO} - failed to inspect container for long label" && ((ret++))

  isula exec -ti annotation echo 1
  [[ $? -ne 0 ]] && msg_err "${FUNCNAME[0]}:${LINENO} - failed to exec in container" && ((ret++))

  check_valgrind_log
  [[ $? -ne 0 ]] && msg_err "${FUNCNAME[0]}:${LINENO} - stop isulad failed" && ((ret++))

  start_isulad_with_valgrind
  [[ $? -ne 0 ]] && msg_err "${FUNCNAME[0]}:${LINENO} - start isulad failed" && ((ret++))

  isula rm -f annotation
  [[ $? -ne 0 ]] && msg_err "${FUNCNAME[0]}:${LINENO} - failed to rm -f container" && ((ret++))

  return ${ret}
}

function do_test_t()
{
  local ret=0
  local runtime=$1
  local test="annotation test => $runtime"
  msg_info "${test} starting..."

  test_label $runtime || ((ret++))
  test_annotation $runtime || ((ret++))

  msg_info "${test} finished with return ${ret}..."
  return $ret
}

declare -i ans=0

for element in ${RUNTIME_LIST[@]};
do
    do_test_t $element
    if [ $? -ne 0 ];then
        let "ans=$ans + 1"
    fi
done

show_result ${ans} "${curr_path}/${0}"
